import React, { useState } from 'react';

const Contact: React.FC = () => {
  const [formData, setFormData] = useState({
    email: '',
    phone: '',
    message: ''
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    // The form will be handled by Formspree
    const form = e.target as HTMLFormElement;
    form.submit();
  };

  return (
    <div className="contact-page">
      {/* Header */}
      <div className="project__header">
        <h1>Contact Me</h1>
        
        {/* Social Icons */}
        <div className="social__icons--contactme">
          <a href="https://github.com/DavidPC4335" className="social__icon--link" target="_blank" rel="noopener noreferrer">
            <i className="fab fa-github"></i>
          </a>
          <a href="https://www.linkedin.com/in/davidpavlovecunsolo/" className="social__icon--link" target="_blank" rel="noopener noreferrer">
            <i className="fab fa-linkedin"></i>
          </a>
          <a href="mailto:davidpc4335@gmail.com" className="social__icon--link" target="_blank" rel="noopener noreferrer">
            <i className="fa-regular fa-envelope"></i>
          </a>
        </div>
      </div>

      {/* Contact Form */}
      <div className="form-wrapper">
        <div className="form-container">
          <form 
            className="form" 
            action="https://formspree.io/f/meqyejqw" 
            method="POST"
            onSubmit={handleSubmit}
          >
            <div className="form-group">
              <label htmlFor="email">
                Email <i className="fa-solid fa-envelope"></i>
              </label>
              <input
                type="email"
                id="email"
                name="Email"
                placeholder="Email Address"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            
            <div className="form-group">
              <label htmlFor="phone">
                Phone <i className="fa-solid fa-phone"></i>
              </label>
              <input
                type="tel"
                id="phone"
                name="Phone"
                placeholder="Phone No."
                value={formData.phone}
                onChange={handleChange}
              />
            </div>
            
            <div className="form-group">
              <label htmlFor="message">
                Message <i className="fa-regular fa-comment-dots"></i>
              </label>
              <textarea
                id="message"
                name="Message"
                placeholder="Message"
                rows={10}
                value={formData.message}
                onChange={handleChange}
                required
              />
            </div>
            
            <button className="form-submit-btn" type="submit">
              Submit
            </button>
            
            <p className="formspree">
              Powered by <a href="https://Formspree.io" target="_blank" rel="noopener noreferrer">Formspree</a>
            </p>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Contact;
