import React from 'react';
import Services from '../components/Services';
import WhyChooseUs from '../components/WhyChooseUs';
import CTABanner from '../components/CTABanner';
import heroBg from '../assets/hero-bg.jpg';
import { pricingPlans } from '../config';

const ServicesPage = () => {
  const deliverPricing = pricingPlans.find(service => service.id === 'delivery')?.startingPrice;
  const movingPricing = pricingPlans.find(service => service.id === 'moving')?.startingPrice;
  const junkRemovalPricing = pricingPlans.find(service => service.id === 'junk')?.startingPrice;

  const servicesDetails = [
    {
      id: 'deliveries',
      title: 'Delivery Services',
      icon: 'fas fa-truck',
      description: 'Fast, reliable delivery solutions for all your needs',
      features: [
        {
          title: 'Same-day delivery available',
          description: 'Urgent deliveries completed within hours'
        },
        {
          title: 'Real-time tracking',
          description: 'Track your delivery from pickup to destination'
        },
        {
          title: 'Flexible scheduling',
          description: 'Choose delivery times that work for you'
        },
        {
          title: 'Local & Regional',
          description: 'Coverage throughout the metro area and beyond'
        }
      ]
    },
    {
      id: 'moving',
      title: 'Moving Services',
      icon: 'fas fa-box',
      description: 'Professional moving solutions for residential and commercial needs',
      features: [
        {
          title: 'Residential Moving',
          description: 'Complete home moving with care and precision'
        },
        {
          title: 'Commercial Moving',
          description: 'Office and business relocation services'
        },
        {
          title: 'Packing Services',
          description: 'Professional packing and unpacking assistance'
        },
        {
          title: 'Furniture Assembly',
          description: 'Disassembly and reassembly of furniture'
        },
        {
          title: 'Storage Solutions',
          description: 'Short and long-term storage options'
        },
        {
          title: 'Long-Distance',
          description: 'Cross-country and interstate moving'
        }
      ]
    },
    {
      id: 'junk-removal',
      title: 'Junk Removal',
      icon: 'fas fa-trash',
      description: 'Efficient and eco-friendly disposal services',
      features: [
        {
          title: 'Household Cleanouts',
          description: 'Complete home decluttering and cleanup'
        },
        {
          title: 'Office Cleanouts',
          description: 'Commercial space clearing and organization'
        },
        {
          title: 'Construction Debris',
          description: 'Removal of renovation and construction waste'
        },
        {
          title: 'Eco-Friendly Disposal',
          description: 'Responsible waste management and recycling'
        },
        {
          title: 'Donation Services',
          description: 'Donate usable items to local charities'
        },
        {
          title: 'Recycling',
          description: 'Proper recycling of eligible materials'
        }
      ]
    }
  ]

  const getPricingForService = (serviceId) => {
    switch(serviceId) {
      case 'deliveries':
        return deliverPricing;
      case 'moving':
        return movingPricing;
      case 'junk-removal':
        return junkRemovalPricing;
      default:
        return '';
    }
  };

  const getPricingDetails = (serviceId) => {
    switch(serviceId) {
      case 'deliveries':
        return {
          startingText: `Starting at ${deliverPricing} for local deliveries`,
          additionalCharges: [
            'Same-day service',
            'Fragile item handling',
            'Long-distance deliveries',
            'After-hours service'
          ]
        };
      case 'moving':
        return {
          startingText: `Starting at ${movingPricing} for local moves`,
          included: [
            'Professional moving team',
            'Packing materials',
            'Furniture protection',
            'Insurance coverage',
            'Loading and unloading'
          ]
        };
      case 'junk-removal':
        return {
          startingText: `Starting at ${junkRemovalPricing} for standard removal`,
          basedOn: [
            'Volume of items',
            'Type of materials',
            'Location and accessibility',
            'Urgency of service'
          ]
        };
      default:
        return {};
    }
  };

  return (
    <div className="services-page">
      <section
        className="page-header"
        style={{
          backgroundImage: `linear-gradient(135deg, rgba(255,255,255,0.95), rgba(207, 207, 207, 0.7)), url(${heroBg})`
        }}
      >
        <div className="container">
          <h1 className="section-title">Our Services</h1>
          <p className="section-subtitle">
            Comprehensive solutions for all your delivery, moving, and junk removal needs
          </p>
          
          <div className="service-navigation">
            {servicesDetails.map(service => (
              <a key={service.id} href={`#${service.id}`} className="service-nav-link">
                <i className={service.icon}></i>
                <span>{service.title}</span>
              </a>
            ))}
          </div>
        </div>
      </section>
      
      <CTABanner />
      
      {servicesDetails.map(service => {
        const pricingDetails = getPricingDetails(service.id);
        
        return (
          <section key={service.id} id={service.id} className="service-section">
            <div className="container">
              <div className="service-section-header">
                <div className="service-icon-large">
                  <i className={service.icon}></i>
                </div>
                <h2>{service.title}</h2>
                <p>{service.description}</p>
              </div>
              
              <div className="service-content">
                <div className="service-features">
                  <h3>{service.id === 'deliveries' ? 'What We Offer' : service.id === 'moving' ? 'Our Moving Services' : 'Junk Removal Services'}</h3>
                  <div className="features-grid">
                    {service.features.map((feature, index) => (
                      <div key={index} className="feature-item">
                        <i className={service.id === 'deliveries' ? 
                          ['fas fa-clock', 'fas fa-box', 'fas fa-shield-alt', 'fas fa-map-marker-alt', 'fas fa-calendar-alt', 'fas fa-route'][index] :
                          service.id === 'moving' ? 
                          ['fas fa-home', 'fas fa-building', 'fas fa-boxes', 'fas fa-tools', 'fas fa-warehouse', 'fas fa-road'][index] :
                          ['fas fa-house-user', 'fas fa-briefcase', 'fas fa-hammer', 'fas fa-leaf', 'fas fa-hand-holding-heart', 'fas fa-recycle'][index]
                        }></i>
                        <h4>{feature.title}</h4>
                        <p>{feature.description}</p>
                      </div>
                    ))}
                  </div>
                </div>
                
                <div className="service-pricing" style={service.id === 'moving' ? {backgroundColor: 'white'} : {}}>
                  <h3>Pricing</h3>
                  <div className="pricing-info">
                    <p><strong>{pricingDetails.startingText}</strong></p>
                    {pricingDetails.additionalCharges && (
                      <>
                        <p>Additional charges may apply for:</p>
                        <ul>
                          {pricingDetails.additionalCharges.map((charge, index) => (
                            <li key={index}>{charge}</li>
                          ))}
                        </ul>
                      </>
                    )}
                    {pricingDetails.included && (
                      <>
                        <p>Included in our service:</p>
                        <ul>
                          {pricingDetails.included.map((item, index) => (
                            <li key={index}>{item}</li>
                          ))}
                        </ul>
                      </>
                    )}
                    {pricingDetails.basedOn && (
                      <>
                        <p>Pricing based on:</p>
                        <ul>
                          {pricingDetails.basedOn.map((item, index) => (
                            <li key={index}>{item}</li>
                          ))}
                        </ul>
                      </>
                    )}
                  </div>
                </div>
              </div>
            </div>
          </section>
        );
      })}
      
      <WhyChooseUs />
    </div>
  );
};

export default ServicesPage;
